import { Robot } from './Robot'
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends Component {
        constructor() {
        super()
        this.state = {
            Robot:Robot,
            Searchfield:""
        }
    }

    onSearchChange(event){
        console.log("==",event);
    }

    render() {
        return(
            <div className = "tc">
                <h1>Robofriends</h1>
                <div className = "pa3">
                    <input className="ba bg-light-blue b--green pa3" type ="search" placeholder = "search robots" onChange={this.onSearchChange} />
                </div>
                <SearchBox searchChange={this.onSearchChange} />
                <CardList Robot={this.state.Robot} searchChange = {this.onSearchChange} />
            </div>
        )
    }
}

export default App;
