import React, { Component } from 'react';
import { Robot } from './Robot'
import CardList from './CardList';
import SearchBox from './SearchBox';
class App extends Component {
        constructor() {
        super()
        this.state = {
            Robot:Robot,
            Searchfield:""
        }
    }

    onSearchChange(event){
        this.setState=({searchfield:event.target.value})
        console.log("==",event.target.value);
        const filterRobot = this.state.Robot.filter(data =>{
            return Robot.name.toLowerCase().include(this.state.searchfield)
        })
    }

    render() {
        return(
            <div className = "tc">
                <h1>Robofriends</h1>
                <SearchBox searchChange={this.onSearchChange} />
                <CardList Robot={this.state.Robot} searchChange = {this.onSearchChange} />
            </div>
        )
    }
}

export default App;
